import React, {useState} from 'react'

const App = () => {
const bgColor={
  backgroundColor:color, 
  width:'100%', 
  height:'100%'
}
  const [color, setColor] = useState('black');
  return (
  <div className={bgColor}>
  <button style={{backgroundColor:'red', color:'white'}} onClick={()=>setColor('red')}>Red</button>
  <button style={{backgroundColor:'green', color:'white'}}>Green</button>
  <button style={{backgroundColor:'blue', color:'white'}}>Blue</button>
  <button style={{backgroundColor:'orange', color:'white'}}>Orange</button>
  </div>
  )
}

export default App
