import React, {useState} from 'react'

const MyComp = () => {
const [fruits, setFruits] = useState(['apple', 'banana', 'mango']);
const addFruits = () =>{
const newFruits = document.getElementById('fruitItems').value;
document.getElementById('fruitItems').value="";
setFruits(newFruits);
}
  return (
    <div>
        <h2>Lists of Fruits</h2>
        <ol>
        {fruits.map((fruit, index) => <li key={index}>{fruit}</li>)}
        </ol>
        <input type="text" id="fruitItems" placeHolder="Add Favorite Fruits" onChange={addFruits}/>
        <button>Add Food</button>
    </div>
  )
}

export default MyComp
