import React from 'react'

const color1={
    fontSize: "2.5em",
    backgroundColor: "hsl(120, 100%, 42%)",
    color: "white",
    padding: "10px",
    borderRadius: "5px",
    margin: "0",
}


const color2={
    fontSize: "2.5em",
    backgroundColor: "hsl(0, 100%, 42%)",   
    color: "white",
    padding: "10px",
    borderRadius: "5px",
    margin: "0",
}
const UserGreetings = (props) => {
    /* if(props.isLoggedIn){

        return <p style={color1}>Welcome {props.username}</p>
        
         }else{
            return <p style={color2}>please login first...</p>
            
         } */

         <p>props.isLoggedIn ? 'Welcome' {props.username} : "please login first..."</p>
}

export default UserGreetings
