import react, {useEffect, useState} from 'react'

const UseEffect =()=>{
    
const[clicked,setClicked] = useState();
const[count,setCount] = useState(0);
/* useEffect(()=>{
alert('we are in homepage')
}, []); */
   

return(
<div>
<button onClick={()=>setClicked('Follow')}>Follow</button><br/><hr/>
<button onClick={()=>setClicked('us on')}>us on</button><br/><hr/>
<button onClick={()=>setClicked('Social Media')}>Social Media</button><hr/>
<h1>{clicked}</h1>
<button onClick={()=>{setCount(count +1)}}>Increment</button>
<h1>{count}</h1>
</div>
)
}

export default UseEffect
