import React, { useState, useEffect } from 'react'
import './Covid.css'
const Covid = () => {

const [data, setData] = useState([]);
const getCovidData = async () =>{
try{
    const response = await fetch('https://data.covid19india.org/data.json');
    const RealData = await response.json();
    console.log(RealData.statewise[0]);
    setData(RealData.statewise[0]);
}
catch(error){
    console.log(error);
}
        
}
useEffect(()=>{
getCovidData();
}, [])

  return (
    <>
    <ul className='hero'>
        <li className='color1'>
            <p><span>Our</span> Country</p>
            <h2>India</h2>
        </li>
        <li className='color2'>
            <p><span>Total</span> Recovered</p>
            <h2>{data.recovered}</h2>
        </li>
        <li className='color3'>
            <p><span>Total</span> Confirmed</p>
            <h2>{data.confirmed}</h2>
        </li>
        <li className='color4'>
            <p><span>Total</span> Deaths</p>
            <h2>{data.deaths}</h2>
        </li>
        <li className='color5'>
            <p><span>Total</span> Active</p>
            <h2>{data.active}</h2>
        </li>
        <li className='6'>
            <p><span>Last</span> UpdateTime</p>
            <h2>{data.lastupdatedtime}</h2>
        </li>
    </ul>
    </>
  )
}

export default Covid
